mapper.select:
SELECT 

FROM z_process_step a

WHERE a.id = #{id}

SELECT 

FROM z_process_step a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.node_name LIKE 
			'%'||#{nodeName}||'%'
			'%'+#{nodeName}+'%'
			concat('%',#{nodeName},'%')


		AND a.step_seq = #{stepSeq}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM z_process_step a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE z_process_step SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
