mapper.select:
SELECT 

FROM z_driver_business a

WHERE a.id = #{id}

SELECT
FROM z_driver_business a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.self_number LIKE '%'||#{selfNumber}||'%' '%'+#{selfNumber}+'%' concat('%',#{selfNumber},'%')
AND a.vehicle_num LIKE '%'||#{vehicleNum}||'%' '%'+#{vehicleNum}+'%' concat('%',#{vehicleNum},'%')
AND a.driver LIKE '%'||#{driver}||'%' '%'+#{driver}+'%' concat('%',#{driver},'%')
AND a.contact_phone LIKE '%'||#{contactPhone}||'%' '%'+#{contactPhone}+'%' concat('%',#{contactPhone},'%')
AND a.id_card LIKE '%'||#{idCard}||'%' '%'+#{idCard}+'%' concat('%',#{idCard},'%')
AND a.driver_date BETWEEN #{beginDriverDate} AND #{endDriverDate}
AND a.quit_date BETWEEN #{beginQuitDate} AND #{endQuitDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM z_driver_business a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE z_driver_business SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
