mapper.select:
SELECT 

FROM z_litigation a

WHERE a.id = #{id}

SELECT
FROM z_litigation a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.vehicle_num LIKE '%'||#{vehicleNum}||'%' '%'+#{vehicleNum}+'%' concat('%',#{vehicleNum},'%')
AND a.acc_time BETWEEN #{beginAccTime} AND #{endAccTime}
AND a.lit_time BETWEEN #{beginLitTime} AND #{endLitTime}
AND a.driver_license LIKE '%'||#{driverLicense}||'%' '%'+#{driverLicense}+'%' concat('%',#{driverLicense},'%')
AND a.query_code LIKE '%'||#{queryCode}||'%' '%'+#{queryCode}+'%' concat('%',#{queryCode},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM z_litigation a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE z_litigation SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
