mapper.select:
SELECT 

FROM z_maintenance_record a

WHERE a.id = #{id}

SELECT
FROM z_maintenance_record a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.self_num LIKE '%'||#{selfNum}||'%' '%'+#{selfNum}+'%' concat('%',#{selfNum},'%')
AND a.vehicle_num LIKE '%'||#{vehicleNum}||'%' '%'+#{vehicleNum}+'%' concat('%',#{vehicleNum},'%')
AND a.inspection_time BETWEEN #{beginInspectionTime} AND #{endInspectionTime}
AND a.driver_license LIKE '%'||#{driverLicense}||'%' '%'+#{driverLicense}+'%' concat('%',#{driverLicense},'%')
AND a.query_code LIKE '%'||#{queryCode}||'%' '%'+#{queryCode}+'%' concat('%',#{queryCode},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM z_maintenance_record a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE z_maintenance_record SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
